mapper.select:
 where TRA_RESULT=0 

    and TRA_NAME like concat(concat('%',#{tarName}),'%')


    and TRA_STATE = #{tarState}


    and TRA_STARTTIME <= #{tarStartTime}


    and TRA_EDDTIME >= #{tarEddTime}

     where TRA_RESULT=0 and TRA_STATE=1
	
	    and TRA_NAME like concat(concat('%',#{tarName}),'%')
	
	
	    and TRA_STARTTIME <= #{tarStartTime}
	
	
	    and TRA_EDDTIME >= #{tarEddTime}

 where TRA_RESULT=0 and TRA_STATE=2

    and TRA_NAME like concat(concat('%',#{tarName}),'%')


    and TRA_STARTTIME <= #{tarStartTime}


    and TRA_EDDTIME >= #{tarEddTime}
